        static public string GenerateRandomNum(int length)
        {
            if (length == 0)
            {
                return "";
            }
            StringBuilder builder = new StringBuilder(length);
            for (int i = 0; i < length;i++ )
            {
                builder.Append(getRandomNum());
            }
            return builder.ToString();
        }
       // rate数字的比例
        static public string GenerateEnName(int length, double rate)
        {
            if (length == 0)
            {
                return "";
            }
            StringBuilder builder = new StringBuilder(length+1);
            builder.Append(englishChar[getRandom().Next(26)]);
            int iRate = (int)rate * 10;
            for (int i = 0; i < length - 1; i++)
            {
                if (getRandom().Next(10) < rate)
                    builder.Append(getRandomNum());
                else
                    builder.Append(getRandomChar());
            }
            return builder.ToString();
        }
