        static public string getRandomMixUserName()
        {
            int baseLenght = 5;
            int enCnt = getRandom().Next(TBConfig.getInstance().enCount);
            if (enCnt == 0)
            {
                enCnt = getRandom().Next(TBConfig.getInstance().enCount);
            }
            int numCnt = getRandom().Next(TBConfig.getInstance().numCount);
            string rntName = make.makeRealNames() + getRandomMixEnAndNum(baseLenght+enCnt, numCnt);
            return rntName;
        }
        // 产生一个随机的密码
        static public string getRandomPwd(int length = 15)
        {
            length = TBConfig.getInstance().pwdLenght;
            StringBuilder builder = new StringBuilder(length + 1);
            builder.Append(englishChar[getRandom().Next(26)]);
            int iRate = 3;
            int iUpper = 6;
            List<int> sysIndexs = new List<int>();
            int symbolscnt = TBConfig.getInstance().symbolsCnt;
            for (int i = 0; i < symbolscnt; i++)
            {
                sysIndexs.Add(getRandom().Next(length));
            }
            for (int i = 0; i < length - 1; i++)
            {
                if (sysIndexs.Contains(i))
                {
                    builder.Append(getRandomSymbol());
                    continue;
                }
                int num = getRandom().Next(10);
                if ( num <= iRate)//数字
                    builder.Append(getRandomNum());
                else if (num <= iUpper && num > iRate)//大写
                {
                    builder.Append(getRandomChar().ToUpper());
                }
                else
                {
                    builder.Append(getRandomChar());
                }
                    
            }
            return builder.ToString();
        }
