        }
       // 产生一个英文和数字的组合
       static public string getRandomMixEnAndNum(int enCnt, int numCnt)
        {
            int totalLen = enCnt+numCnt;
            StringBuilder builder = new StringBuilder(totalLen+1);
            List<int> enList = new List<int>();
            for (int i = 0; i < enCnt; i++ )
            {
                enList.Add(getRandom().Next(totalLen));
            }
            int rand = getRandom().Next(enCnt);
            if (rand == 1 || rand ==0)
            {
               rand = 2;
            }
            for (int k = 0; k < totalLen; k++ )
            {
                if (enList.Contains(k))
                {
                    string ch = getRandomChar();
                    if ((k % rand) == 0)
                    {
                        ch = ch.ToUpper();
                    }
                    builder.Append(ch);
                }
                else
                    builder.Append(getRandomNum());
            }
            return builder.ToString();
        }
