       //从一级GBK字库中取字:
       public string makeGBKNamesBy3755(int k, string lastName)
       {
           string rs = "";
           Random rd = RandomGenerator.getRandom();
           string w3755 = GBK3755();
           for (int i = 0; i < k; i++)
           {
               int r1 = rd.Next(0, 3755);
               string n1 = w3755.Substring(r1, 1);
               int r2 = rd.Next(0, 3755);
               string n2 = w3755.Substring(r2, 1);
               rs += lastName + n1 + n2 + ",";
           }
           return rs;
       }

