       //从一级Unicode字库中取字:
       public string makeUnicodeNames(int k, string lastName)
       {
           string rs = "";
           Random rd = RandomGenerator.getRandom();
           for (int i = 0; i < k; i++)
           {
               long r1 = rd.Next(19968, 19968 + 20902);
               char n1 = (char)r1;
               long r2 = rd.Next(19968, 19968 + 20902);
               char n2 = (char)r2;
               rs += lastName + n1 + n2 + ",";
           }
           return rs;
       }

       //一级GBK字库:
       public string GBK3755()
       {
           byte[] bt; string rlt = "";
           for (byte i = 160 + 16; i <= 160 + 54; i++)
           {
               for (byte j = 160 + 1; j <= 160 + 94; j++)
               {
                   bt = new byte[] { i, j };
                   string str = Encoding.GetEncoding("GBK").GetString(bt);
                   rlt += str;
               }
           }
           for (byte j = 160 + 1; j <= 160 + 90; j++)
           {
               bt = new byte[] { 160 + 55, j };
               string str = Encoding.GetEncoding("GBK").GetString(bt);
               rlt += str;
           }
           return rlt;
       }

       //二级GBK字库:
       public string GBK3008()
       {
           byte[] bt; string rlt = "";
           for (byte i = 160 + 56; i <= 160 + 87; i++)
           {
               for (byte j = 160 + 1; j <= 160 + 94; j++)
               {
                   bt = new byte[] { i, j };
                   string str = Encoding.GetEncoding("GBK").GetString(bt);
                   rlt += str;
               }
           }
           return rlt;
